package Orange_HRM_Login;

public class Break_continue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=0;i<=10;i++) 
{
	if(i==5)
	{
		break;
	}
	System.out.println("i");
}
    System.out.println("hello");
    for(int i=0;i<=10;i++) 
    {
    	if(i==5)
    	{
    		continue;
    	}
    	System.out.println("i");
    }
        System.out.println("hello");
	}

}
